import { useEffect, useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import axios from "axios"
import { UserAuth } from './Authentication/User.js'
import Pagination from "./Pagination.js"
import './app.css'
// GET Function
function EmpData() {
    const [data, setData] = useState([])
    useEffect(() => {
        fetch("http://localhost:3006/Employee", {
            method: "GET"
        }).then((res) => {
            return res.json()
        }).then((resp) => {
            setData(resp)
        })
    }, [])
    const [value, setValue] = useState("")
    const navigate = useNavigate()
    const chnageValue = ((e) => {
        setValue(e.target.value)
    })
    //Add to cart
    const [cart, setCart] = useState([])
    const addToCart = (id) => {
        fetch("http://localhost:3006/Employee/" + id)
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                console.log(resp)
                fetch("http://localhost:3006/Data", {
                    method: "POST",
                    headers: { "Content-type": "application/json" },
                    body: JSON.stringify(resp)
                })
                    .then(() => {
                        alert("Added into Cart")
                    })
            })
    }
    useEffect(() => {
        fetch("http://localhost:3006/Data")
            .then((res) => {
                return res.json()
            }).then((resp) => {
                setCart(resp)
            })
    })
    //pagenation Method
    const [page, setPage] = useState(1)
    const [record, setRecord] = useState(4)
    let lr = page * record  //4
    let fr = lr - record //0
    let mydata = data.slice(fr, lr)
    const change = (n) => {
        setPage(n)
    }
    const prev = () => {
        setPage(page - 1)
    }
    const next = () => {
        setPage(page + 1)
    }
    //Sort Method
    const option = ["name", "cost", "available"]
    const [sort, setSort] = useState([])
    const currentUser = UserAuth()
    const sortData = async (e) => {
        e.preventDefault()
        let value = e.target.value
        setSort(value)
        return await axios.get(`http://localhost:3006/Employee?_sort=${value}&_order=asc`)
            .then((res) => {
                setData(res.data)
            })
    }
    //Search Function
    const FilterData = async (e) => {
        e.preventDefault()
        return await axios.get(`http://localhost:3006/Employee?q=${value}`)
            .then((res) => {
                setData(res.data)
            })
