import { useEffect, useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import axios from "axios"
import {UserAuth} from './Authentication/User.js'
import Pagination from "./Pagination.js"
// GET Function
function EmpData() {
    const [data, setData] = useState([])
    useEffect(() => {
        fetch("http://localhost:3006/Employee", {
            method: "GET"
        }).then((res) => {
            return res.json()
        }).then((resp) => {
            setData(resp)
        })
    },[])
    const [value, setValue] = useState("")
    const navigate = useNavigate()
    const chnageValue = ((e) => {
        setValue(e.target.value)
    })
 //Add to cart
    const addToCart=(id)=>{
fetch("http://localhost:3006/Employee/"+id)
.then((res))
console.log("Clicked")
    }

//pagenation Method
const[page,setPage]=useState(1)
const[record,setRecord]=useState(4)
let lr=page*record  //4
let fr=lr-record //0
let mydata=data.slice(fr,lr)
const change=((n)=>{
    setRecord(n)
})
//Sort Method
    const option=["name","cost","available"]
    const[sort,setSort]=useState("")
    const currentUser=UserAuth()
    const sortData=async(e)=>{
        e.preventDefault()
        let value=e.target.value
        setSort(value)
        return await axios.get(`http://localhost:3006/Employee?_sort=${value}&_order=asc`)
            .then((res) => {
                setData(res.data)
            })
    }
    //Search Function
    const FilterData = async (e) => {
        e.preventDefault()
        return await axios.get(`http://localhost:3006/Employee?q=${value}`)
            .then((res) => {
                setData(res.data)
            })
    }
    //reset method
    const resetData = async (e) => {
        e.preventDefault()
        return await axios.get(`http://localhost:3006/Employee`)
            .then((res) => {
                setData(res.data)
            })
    }

    //Edit Method
    const changeeId = (id) => {
        navigate("/edit/" + id)
    }
//Delete Method
    const changeDelete = (id) => {
        fetch("http://localhost:3006/Employee/" + id, {
            method: "DELETE"
        }).then(() => {
            alert("delete successfully")
            window.location.reload()
        }).catch(() => {
            alert("Error")
        })
    }
    return (
        <div>
            <div>
                <nav className="navbar bg-body-tertiary">
                    <div className="container-fluid">
                        <h1><span style={{ color: "orange" }}>MOBILE</span>&nbsp;<span style={{ color: "green" }}>SHOPPING</span></h1>
                       <button style={{borderRadius:"100px"}}>{currentUser?.email}</button>
                        <form onSubmit={FilterData} className="d-flex">
                            <input value={value} onChange={chnageValue} className="form-control" type="text" placeholder="Search" aria-label="Search" />
                            <button className="btn btn-outline-success" type="submit">Search</button>
                            <button className="btn btn-outline-success" onClick={resetData}>Reset</button>
                        </form>
                    </div>
                </nav>
            </div>
            <Link to='/form' className="btn btn-success">Add New</Link><br /><br />
            <div style={{ display: "grid", gridTemplateColumns: "20% 80%" }}>
                <div>
                    <div className="card m-2 p-3 " style={{backgroundColor:"grey"}}>
                      <select>
                        <option>View Mobiles</option>
                      </select><br/>
                      <select>
                        <option>View Cost Type</option>
                      </select><br/>
                      <select>
                        <option>Availble Mobiles</option>
                      </select><br/>
                      <select>
                        <option>More</option>
                      </select>
                      </div>
                    <div className="card m-3 p-3" style={{backgroundColor:"grey"}}>
                        <select value={sort} onChange={sortData}>
                            <option>---Choose---</option>
                            {option.map((i)=>(
                                <option>{i}</option>
                            ))}
                        </select>
                    </div>
                    <div className="card m-2 p-2">
                       <label>Rs.0 to Rs.100</label>
                       <input type="range"></input>
                       <label>Price</label>
                    </div>
                    <div className="card m-2 p-2">
                        <label>0% to 100%</label>
                        <input type="range"></input>
                        <label>Offers%</label>
                    </div>
                </div>
                <div className="container text-center" style={{ display: "grid", gridTemplateColumns: "auto auto auto auto" }}>
                    {mydata.map((item) => (
                        <div className="card  m-1 p-2" style={{ width: "250px", border: "1px solid blue", borderRadius: "2rem" }}>
                            <img src={item.url} alt="Mobile" style={{ widhth: "100px", height: "180px", borderRadius: "20px"}} />
                            <div className="card-body">
                                <p>Name:{item.name}</p>
                                <p>Cost:{item.cost}</p>
                                <p>Availble:{item.available}</p>
                                <a  className="btn btn-success" onClick={()=>{addToCart(id)}}>Add to Cart</a><br /><br />
                                <a onClick={() => { changeeId(item.id) }} className="btn btn-primary">
                                    Edit
                                </a>
                                <a onClick={() => { changeDelete(item.id) }} className="btn btn-danger">Delete</a>
                            </div>
                        </div>
                    ))}
                    
                </div></div><br />
                <Pagination total={data.length} record={record} update={change}/>
            <div style={{ display: "flex", justifyContent: "space-between", backgroundColor:"grey", color: "white", padding: "5px" }}>
                <div>
                    <h4>LOGO</h4>
                    <p>Subline</p>
                </div>
                <div>
                    <h4>Pages</h4>
                    <p>About us</p>
                    <p>Our Expertise</p>
                    <p>Testimonials</p>
                    <p>Skin&Hair</p>
                    <p>Shop</p>
                </div>
                <div>
                    <h4>Legal& Help</h4>
                    <p>FAQs</p>
                    <p>Terms of use</p>
                    <p>Privacy Policy</p>
                </div>
                <div>
                    <h4>Contact Us</h4>
                    <i class="icofont-address">Address</i><br />
                    <i class="icofont-phone">Phone Numbers</i><br />
                    <i class="icofont-email">Email Id</i>
                </div>
                <div>
                    <h4>Social Links</h4>
                    &#xed7a;
                    <i class="icofont-linkedin"></i>
                    &#xed4d;
                </div>
            </div>

        </div>
    )
}
export default EmpData;
