import { useState,useEffect } from "react"

const[add,setAdd]=useState("")
function AddCart(){
    useEffect(() => {
        fetch("http://localhost:3006/Data")
            .then((res) => {
                return res.json()
            }).then((resp) => {
                setAdd(resp)
            })
    })
    const changeDel = (id) => {
        fetch("http://localhost:3006/Data/" + id, {
            method: "DELETE"
        }).then(() => {
            alert("delete successfully")
            window.location.reload()
        }).catch(() => {
            alert("Error")
        })
    }
    return(
        <div>
<h1 style={{ color: "green", textAlign: "center" }}>ADD TO CART</h1>
                            <h2>Cart Length is:{add.length}</h2>
                            <table className="table table-bordered">
                                <thead>
                                    <tr>
                                        <th>Id</th>
                                        <th>Name</th>
                                        <th>Cost</th>
                                        <th>Available</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody className="p-2">
                                    {add.map((i) => (
                                        <tr>
                                            <td>{i.id}</td>
                                            <td>{i.name}</td>
                                            <td>{i.cost}</td>
                                             <td>{i.available}</td>
                                            <button className="btn btn-danger" onClick={changeDel}>Remove</button>
                                        </tr>
                                     ))}
                                 </tbody>
                            </table>
        </div>
     )
 }
 export default AddCart;
